mapper.select:
select USER_NAME name,PHONE phone,EMAIL email,DEPT dept
from t_users
where USER_ID=#{TEACHER_ID,jdbcType=VARCHAR}

select USER_NAME name,PHONE phone,EMAIL email,DEPT dept
from t_users
where USER_ID=#{CREATOR_ID,jdbcType=VARCHAR}

(select
,IFNULL(temp_less_student.S_status,"PUB")
joinStatus,temp_less_student.REJ_REASON
from t_lesson ,(select DISTINCT LESS_ID tempLESS_id,S_STATUS,REJ_REASON from t_less_student where
S_USER_ID=#{userId,jdbcType=VARCHAR}) temp_less_student
WHERE t_lesson.LESS_ID=temp_less_student.tempLESS_id and L_STATUS!=0
and t_lesson.TEACHER_ID!=#{userId,jdbcType=VARCHAR}

    and L_TYPE=#{lessonType,jdbcType=VARCHAR}


    and L_NAME like #{lessonNameKeyword,jdbcType=VARCHAR}

)
union
(select
,'TEA' joinStatus,'' REJ_REASON
from t_lesson
where TEACHER_ID=#{userId,jdbcType=VARCHAR} and L_STATUS!=0

    and L_TYPE=#{lessonType,jdbcType=VARCHAR}

)
order by stime desc

(select
,IFNULL(temp_less_student.S_status,"PUB")
joinStatus,temp_less_student.REJ_REASON
from t_lesson left join (select LESS_ID tempLESS_id,S_STATUS,REJ_REASON from t_less_student where
S_USER_ID=#{userId,jdbcType=VARCHAR}) temp_less_student
on t_lesson.LESS_ID=temp_less_student.tempLESS_id
where L_STATUS!=0 and t_lesson.TEACHER_ID!=#{userId,jdbcType=VARCHAR}

    and L_TYPE=#{lessonType,jdbcType=VARCHAR}


    and L_NAME like #{lessonNameKeyword,jdbcType=VARCHAR}

)
union
(select
,'TEA' joinStatus,'' REJ_REASON
from t_lesson
where TEACHER_ID=#{userId,jdbcType=VARCHAR} and L_STATUS!=0

    and L_TYPE=#{lessonType,jdbcType=VARCHAR}

)
order by stime desc

select * from (select ,IFNULL(temp_less_student.S_STATUS,"PUB") joinStatus ,temp_less_student.REJ_REASON from t_lesson left join (select LESS_ID tempLESS_id,S_STATUS,REJ_REASON from t_less_student where S_USER_ID=#{userId,jdbcType=VARCHAR}) temp_less_student on t_lesson.LESS_ID=temp_less_student.tempLESS_id where L_STATUS!=0 and t_lesson.TEACHER_ID!=#{userId,jdbcType=VARCHAR}
and L_TYPE=#{lessonType,jdbcType=VARCHAR}
) middle where joinStatus=#{oprType,jdbcType=VARCHAR} order by stime desc

select ,'TEA' joinStatus from t_lesson where TEACHER_ID=#{userId,jdbcType=VARCHAR} and L_STATUS!=0
and L_TYPE=#{lessonType,jdbcType=VARCHAR}
and L_NAME like #{lessonNameKeyword,jdbcType=VARCHAR}
order by stime desc

select
from t_lesson where LESS_ID=#{lessonId,jdbcType=VARCHAR}
and L_STATUS!=0

select IFNULL(S_STATUS,"PUB")
from t_less_student
where S_USER_ID=#{userId,jdbcType=VARCHAR} and LESS_ID=#{lessonId,jdbcType=VARCHAR}

mapper.update:
update t_less_student

    S_STATUS = 'CONF'

where S_USER_ID=#{userId,jdbcType=VARCHAR} and LESS_ID=#{lessonId,jdbcType=VARCHAR}

update t_lesson

    L_STATUS = #{lessonStatus,jdbcType=INTEGER},
    
        UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP}
    

where LESS_ID=#{lessonId,jdbcType=VARCHAR}

mapper.select:
select count(*)
from t_leave
where USER_ID=#{userId,jdbcType=VARCHAR} and SCH_ID=#{schId,jdbcType=VARCHAR}
